mapper.update:
update local_meta_data_column
set remarks = #{remarks,jdbcType=VARCHAR}
where id = #{id}

mapper.delete:
delete from local_meta_data_column
where local_meta_data_db_id = #{localMetaDataDbId,jdbcType=INTEGER}
and EXISTS (select 1 from local_meta_data lmd where lmd.id = #{localMetaDataDbId,jdbcType=INTEGER} and lmd.`status` != 2)
