mapper.delete:
update local_meta_data
set status = 4
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into local_meta_data (file_id, meta_data_id,meta_data_name, status, publish_time,
  remarks, industry,owner
  )
values (#{fileId,jdbcType=INTEGER}, #{metaDataId,jdbcType=VARCHAR},
  #{metaDataName,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{publishTime,jdbcType=TIMESTAMP},
  #{remarks,jdbcType=VARCHAR}, #{industry,jdbcType=INTEGER},#{owner}
  )

mapper.update:
update local_meta_data
set status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

update.foreach:
update local_meta_data
set status = #{item.status,jdbcType=INTEGER},
    publish_time =  #{item.publishTime,jdbcType=TIMESTAMP},
    rec_update_time =  #{item.recUpdateTime,jdbcType=TIMESTAMP}
where meta_data_id = #{item.metaDataId,jdbcType=VARCHAR}

mapper.update:
update local_meta_data
set data_token_id = #{dataTokenId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select from local_meta_data where status != 4 and (data_token_id is null or data_token_id = '') and owner = #{userAddress}
and meta_data_name like concat('%',#{keyword,jdbcType=VARCHAR},'%')
ORDER BY rec_update_time desc
