mapper.delete:
update local_meta_data
set status = 4
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into local_meta_data (file_id, meta_data_id,meta_data_name, status, publish_time,
  remarks, industry,owner,meta_data_type
  )
values (#{fileId,jdbcType=INTEGER}, #{metaDataId,jdbcType=VARCHAR},
  #{metaDataName,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{publishTime,jdbcType=TIMESTAMP},
  #{remarks,jdbcType=VARCHAR}, #{industry,jdbcType=INTEGER},#{owner},#{metaDataType}
  )

mapper.update:
update local_meta_data
set status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

update local_meta_data
set data_token_id = #{dataTokenId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select from local_meta_data where status in (2,7) and owner = #{userAddress}
and meta_data_name like concat('%',#{keyword,jdbcType=VARCHAR},'%')
ORDER BY rec_update_time desc
