mapper.update:
update local_power_node
node_name = #{nodeName, jdbcType=VARCHAR}, internal_ip = #{internalIp, jdbcType=VARCHAR}, internal_port = #{internalPort, jdbcType=INTEGER}, external_ip = #{externalIp, jdbcType=VARCHAR}, external_port = #{externalPort, jdbcType=INTEGER}, start_time = #{startTime, jdbcType=TIMESTAMP}, remarks = #{remarks, jdbcType=VARCHAR}, conn_message = #{connMessage, jdbcType=VARCHAR}, power_id = #{powerId, jdbcType=VARCHAR}, power_status = #{powerStatus, jdbcType=INTEGER}, conn_time = #{connTime, jdbcType=TIMESTAMP}, conn_status = #{connStatus, jdbcType=INTEGER}, memory = #{memory, jdbcType=BIGINT}, core = #{core, jdbcType=INTEGER}, bandwidth = #{bandwidth, jdbcType=BIGINT}, used_memory = #{usedMemory, jdbcType=BIGINT}, used_core = #{usedCore, jdbcType=INTEGER}, used_bandwidth = #{usedBandwidth, jdbcType=BIGINT},
where node_id = #{nodeId, jdbcType=VARCHAR}

update local_power_node
set node_name = #{nodeName,jdbcType=VARCHAR}
where node_Id = #{nodeId,jdbcType=VARCHAR}

mapper.select:
SELECT t1.node_id, CAST(t1.used_core / t2.core * 100 as UNSIGNED) as core_pct, CAST(t1.used_memory / t2.memory * 100 as UNSIGNED) as memory_pct, CAST(t1.used_bandwidth / t2.bandwidth * 100 as UNSIGNED) as bandwidth_pct
FROM (
  SELECT lpjk.node_id, sum(lpjk.used_core) as used_core, sum(lpjk.used_memory) as used_memory, sum(lpjk.used_bandwidth) as used_bandwidth
  FROM  local_power_join_task lpjk
  WHERE lpjk.node_id = #{nodeId, jdbcType=VARCHAR}
) t1, (
  SELECT lpd.core, lpd.memory, lpd.bandwidth
  FROM local_power_node lpd
  WHERE lpd.node_id = #{nodeId, jdbcType=VARCHAR}
) t2
