mapper.update:
update local_seed_node
internal_ip = #{internalIp, jdbcType=VARCHAR}, internal_port = #{internalPort, jdbcType=INTEGER}, conn_status = #{connStatus, jdbcType=INTEGER}, init_flag = #{initFlag, jdbcType=INTEGER},
where seed_node_id = #{seedNodeId, jdbcType=VARCHAR}

update.foreach:
update local_seed_node
internal_ip = #{item.internalIp, jdbcType=VARCHAR}, internal_port = #{item.internalPort, jdbcType=INTEGER}, conn_status = #{item.connStatus, jdbcType=INTEGER}, init_flag = #{item.initFlag, jdbcType=INTEGER},
where seed_node_id = #{item.seedNodeId, jdbcType=VARCHAR}
