mapper.update:
update local_seed_node
out_node_id = #{outNodeId, jdbcType=VARCHAR}, internal_ip = #{seedNodeName, jdbcType=VARCHAR}, conn_status = #{connStatus, jdbcType=INTEGER}, init_flag = #{initFlag, jdbcType=INTEGER},
where seed_node_address = #{seedNodeAddress, jdbcType=VARCHAR}

update.foreach:
update local_seed_node
out_node_id = #{item.outNodeId, jdbcType=VARCHAR}, internal_ip = #{item.seedNodeName, jdbcType=VARCHAR}, conn_status = #{item.connStatus, jdbcType=INTEGER}, init_flag = #{item.initFlag, jdbcType=INTEGER},
where seed_node_address = #{seedNodeAddress, jdbcType=VARCHAR}
