mapper.select:
select

from meta_data
where meta_data_name = #{resourceName}
  and status != 101
  and `user` = #{address}

mapper.delete:
update meta_data
set status = 101
where id = #{id,jdbcType=INTEGER}

mapper.update:
update meta_data
set status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select md.* from meta_data md left join data_token dt on md.id = dt.meta_data_db_id where md.status = 2 and dt.status = 2 and md.`user` = #{userAddress}
and md.meta_data_name like concat('%' , #{keyword,jdbcType=VARCHAR} , '%')
ORDER BY md.rec_update_time desc

select md.* from meta_data md left join attribute_data_token adt on md.id = adt.meta_data_db_id where md.status = 2 and adt.status = 2 and md.`user` = #{userAddress}
and md.meta_data_name like concat('%' , #{keyword,jdbcType=VARCHAR} , '%')
ORDER BY md.rec_update_time desc
