mapper.select:
select 

from task_data_provider
where task_id = #{taskId,jdbcType=VARCHAR}
  and meta_data_id = #{metaDataId,jdbcType=VARCHAR}

mapper.delete:
delete from task_data_provider
where task_id = #{taskId,jdbcType=VARCHAR}
  and meta_data_id = #{metaDataId,jdbcType=VARCHAR}

mapper.insert:
insert into task_data_provider (task_id, meta_data_id, identity_id, party_id )
values (#{taskId,jdbcType=VARCHAR}, #{metaDataId,jdbcType=VARCHAR}, #{identityId,jdbcType=VARCHAR}, #{partyId,jdbcType=VARCHAR}}
  )
