mapper.select:
SELECT t.id, t.task_Id, t.task_Name, t.`status`, t.create_At, t.reviewed, t.role FROM task t
and status = #{status,jdbcType=VARCHAR}
and role = #{role,jdbcType=VARCHAR}
and create_At >= #{startTimestamp,jdbcType=TIMESTAMP}
and create_At <= #{endTimestamp,jdbcType=TIMESTAMP}
and task_Name like CONCAT(CONCAT('%',#{keyWord}),'%')
order by create_At DESC

mapper.update:
update task
set reviewed = #{reviewed,jdbcType=BIT}
where task_Id = #{taskId,jdbcType=VARCHAR}
