mapper.select:
select 

from task_power_provider
where task_id = #{taskId,jdbcType=VARCHAR}
  and identity_id = #{identityId,jdbcType=VARCHAR}

mapper.delete:
delete from task_power_provider
where task_id = #{taskId,jdbcType=VARCHAR}
  and identity_id = #{identityId,jdbcType=VARCHAR}

mapper.insert:
insert into task_power_provider (task_id, identity_id, used_core, 
  used_memory, used_Bandwidth, rec_update_time
  )
values (#{taskId,jdbcType=VARCHAR}, #{identityId,jdbcType=VARCHAR}, #{usedCore,jdbcType=INTEGER}, 
  #{usedMemory,jdbcType=BIGINT}, #{usedBandwidth,jdbcType=BIGINT}, #{recUpdateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update task_power_provider
set used_core = #{usedCore,jdbcType=INTEGER},
  used_memory = #{usedMemory,jdbcType=BIGINT},
  used_Bandwidth = #{usedBandwidth,jdbcType=BIGINT},
  rec_update_time = #{recUpdateTime,jdbcType=TIMESTAMP}
where task_id = #{taskId,jdbcType=VARCHAR}
  and identity_id = #{identityId,jdbcType=VARCHAR}

