mapper.select:
select

from task_result_consumer
where task_id = #{taskId,jdbcType=VARCHAR}
  and consumer_identity_id = #{consumerIdentityId,jdbcType=VARCHAR}

mapper.delete:
delete from task_result_consumer
where task_id = #{taskId,jdbcType=VARCHAR}
  and consumer_identity_id = #{consumerIdentityId,jdbcType=VARCHAR}
